import React, { Component } from 'react';
import Resume from "./components/resume";
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();

    this.state = {
      generalshow: false,
      edushow: false,
      workshow: false,
      generalin: {
        first: '',
        last: '',
        address: '',
        phone: '',
        email: '',
        role: '',
        description: '',
      },
      educationin: [{
        school: '',
        degree: '',
        start: '',
        end: '',
        location: '',
        description: '',
      }],
      workin: [{
        company: '',
        role: '',
        start: '',
        end: '',
        location: '',
        description: '',
      }],
      storedforms: {
        generalstore: [],
        educationstore: [],
        workstore: []
      }
    }
  }

  genClick() {

  }


  render() {
    const { generalin, educationin, workin, storedforms } = this.state;
    return (
    <div className='cvapp'>
      <div className='formscontainer'>
        <form id="genform" onSubmit={this.onSubmitGen}>
          <div id="gentitle">General Information</div>
          <label htmlFor='firstin'>First Name</label>
          <input type="text" id="firstin"></input>
          <label htmlFor='lastin'>Last Name</label>
          <input type="text" id="lastin"></input>
          <label htmlFor='addressin'>Address</label>
          <input type="text" id="addressin"></input>
          <label htmlFor='phonein'>Phone</label>
          <input type="text" id="phonein"></input>
          <label htmlFor='emailin'>Email</label>
          <input type="text" id="emailin"></input>
          <label htmlFor='rolein'>Role</label>
          <input type="text" id="rolein"></input>
          <label htmlFor='profilein'>Profile Description</label>
          <input type="textarea" id="profilein"></input>
          <button id="gensubmit" type="submit" value="Submit"></button>
        </form>
        <div id="eduformcontainer"></div>
        <div id="workformcontainer"></div> 
        <form id="eduform" onSubmit={this.onSubmitEdu}>

        </form>
      </div>
      <div className='editcontainer'>
        <button onClick={this.genClick} className='editbtn editgeneral'>Edit Info</button>
        <button onClick={this.eduClick} className='editbtn editeduc'>Edit Education</button>
        <button onClick={this.workClick} className='editbtn editwork'>Edit Work</button>
      </div>
      <Resume forms={storedforms} />
    </div>
    )
  }

}

export default App;
